import { onMounted, ref, watch, toRefs, reactive } from "vue";
import { insertSaler } from "../../../../network/saler";
export default {
  name: "adminInsertSaler",

  setup() {
    let insertSuccess = ref(false);
    let insertFailed = ref(false);
    let form = reactive({
      name: "",
      email: "",
      password: "",
      phone: ""
    });
    let valid = true;
    let nameRules = [v => !!v || 'Name is required', v => v && v.length <= 10 || 'Name must be less than 10 characters'];
    let emailRules = [v => !!v || 'E-mail is required', v => /.+@.+\..+/.test(v) || 'E-mail must be valid'];
    let passwordRules = [v => !!v || 'E-mail is required'];
    let phoneRules = [v => !!v || 'Name is required', v => v && v.length !== 11 || 'Name must be less than 10 characters'];

    const validate = () => {
      insertSaler(form).then(res => {
        let status = res.data.status;
        console.log(res);
        console.log(res.data.status);

        if (status === 1) {
          insertSuccess.value = true;
          setTimeout(() => {
            insertSuccess.value = false;
          }, 1000);
        } else {
          insertFailed.value = true;
          setTimeout(() => {
            insertFailed.value = false;
          }, 1000);
        }

        form.name = "";
        form.email = "";
        form.password = "";
        form.phone = "";
      });
    };

    return {
      valid,
      nameRules,
      emailRules,
      passwordRules,
      phoneRules,
      ...toRefs(form),
      validate,
      insertSuccess,
      insertFailed
    };
  }

};
