import { createStore } from 'vuex'

export default createStore({
  state: {
    user: {
      isLogin: window.localStorage.getItem('JWT_Token') ? true : false,
    }
  },
  getters: {
  },
  mutations: {
    setIsLogin(state, payload) {
      state.user.isLogin = payload;
    }
  },
  actions: {
  },
  modules: {
  }
})
