import {request} from "./request";

export function getSalers(page) {
    return request({
        url:`/salers/${page}`,
        method:'get',
    })
}

export function deleteSalersById(id) {
    return request({
        url:`/salers/${id}`,
        method:'delete',
    })
}

export function betchDeleteSalersById(data) {
    return request({
        url:'/salers/batchDelete',
        method:'delete',
        headers: {'content-type': 'application/json'},
        data: JSON.stringify(data)
    })
}

export function insertSaler(data) {
    return request({
        url:'/salers/insertSaler',
        method:'post',
        headers: {'content-type': 'application/json'},
        data: JSON.stringify(data)
    })
}

export function getSalerById(id) {
    return request({
        url:`/saler_id/${id}`,
        method:'get',
    })
}

export function updateSaler(data) {
    return request({
        url:'/updateSaler',
        method:'post',
        headers: {'content-type': 'application/json'},
        data: JSON.stringify(data)
    })
}

export function uploadSalerImg(fileid, file) {
    return request({
        url:'/updateSalerImg',
        method:'post',
        headers: {'content-type': 'multipart/form-data'},
        fileid, file
    })
}
